/* LIGHT MODE */
:root {
  --button-outline: rgba(0,0,0, .10);
  --badge-outline: rgba(0,0,0, .05);

  /* Automatic computation of border around primary / danger buttons */
  --opaque-button-border-intensity: -8; /* In terms of percentages */

  /* Backgrounds applied on top of other backgrounds when hovered/active */
  --elevate-1: rgba(0,0,0, .03);
  --elevate-2: rgba(0,0,0, .08);

  --background: 210 5% 98%;

  --foreground: 215 16% 12%;

  --border: 210 12% 88%;

  --card: 210 5% 96%;

  --card-foreground: 215 16% 12%;

  --card-border: 210 10% 90%;

  --sidebar: 210 6% 94%;

  --sidebar-foreground: 215 16% 12%;

  --sidebar-border: 210 10% 87%;

  --sidebar-primary: 173 82% 40%;

  --sidebar-primary-foreground: 210 100% 98%;

  --sidebar-accent: 173 25% 88%;

  --sidebar-accent-foreground: 215 16% 15%;

  --sidebar-ring: 173 82% 40%;

  --popover: 210 6% 92%;

  --popover-foreground: 215 16% 12%;

  --popover-border: 210 10% 85%;

  --primary: 173 82% 40%;

  --primary-foreground: 210 100% 98%;

  --secondary: 210 12% 86%;

  --secondary-foreground: 215 16% 15%;

  --muted: 210 10% 90%;

  --muted-foreground: 215 12% 35%;

  --accent: 210 15% 88%;

  --accent-foreground: 215 16% 15%;

  --destructive: 358 75% 48%;

  --destructive-foreground: 210 100% 98%;

  --input: 210 16% 78%;
  --ring: 173 82% 40%;
  --chart-1: 173 82% 40%;
  --chart-2: 183 78% 45%;
  --chart-3: 163 85% 42%;
  --chart-4: 193 75% 50%;
  --chart-5: 153 70% 48%;

  /* Success/Teal accent for confirmations */
  --success: 173 82% 42%;
  --success-foreground: 210 100% 98%;

  /* Warning/Amber accent for alerts */
  --warning: 32 95% 52%;
  --warning-foreground: 215 16% 12%;

  --font-sans: Inter, system-ui, -apple-system, sans-serif;
  --font-serif: Georgia, serif;
  --font-mono: 'Roboto Mono', Menlo, monospace;
  --radius: .5rem;
  --shadow-2xs: 0px 1px 2px 0px hsl(210 10% 10% / 0.04);
  --shadow-xs: 0px 1px 3px 0px hsl(210 10% 10% / 0.06);
  --shadow-sm: 0px 2px 4px 0px hsl(210 10% 10% / 0.05), 0px 1px 2px -1px hsl(210 10% 10% / 0.04);
  --shadow: 0px 4px 6px -1px hsl(210 10% 10% / 0.07), 0px 2px 4px -2px hsl(210 10% 10% / 0.05);
  --shadow-md: 0px 6px 12px -2px hsl(210 10% 10% / 0.08), 0px 3px 6px -3px hsl(210 10% 10% / 0.06);
  --shadow-lg: 0px 10px 20px -4px hsl(210 10% 10% / 0.10), 0px 4px 8px -4px hsl(210 10% 10% / 0.07);
  --shadow-xl: 0px 20px 30px -6px hsl(210 10% 10% / 0.12), 0px 8px 16px -6px hsl(210 10% 10% / 0.08);
  --shadow-2xl: 0px 25px 50px -12px hsl(210 10% 10% / 0.15);
  --tracking-normal: 0em;
  --spacing: 0.25rem;

  /* Automatically computed borders - intensity can be controlled by the user by the --opaque-button-border-intensity setting */

  /* Fallback for older browsers */
  --sidebar-primary-border: hsl(var(--sidebar-primary));
  --sidebar-primary-border: hsl(from hsl(var(--sidebar-primary)) h s calc(l + var(--opaque-button-border-intensity)) / alpha);

  /* Fallback for older browsers */
  --sidebar-accent-border: hsl(var(--sidebar-accent));
  --sidebar-accent-border: hsl(from hsl(var(--sidebar-accent)) h s calc(l + var(--opaque-button-border-intensity)) / alpha);

  /* Fallback for older browsers */
  --primary-border: hsl(var(--primary));
  --primary-border: hsl(from hsl(var(--primary)) h s calc(l + var(--opaque-button-border-intensity)) / alpha);

  /* Fallback for older browsers */
  --secondary-border: hsl(var(--secondary));
  --secondary-border: hsl(from hsl(var(--secondary)) h s calc(l + var(--opaque-button-border-intensity)) / alpha);

  /* Fallback for older browsers */
  --muted-border: hsl(var(--muted));
  --muted-border: hsl(from hsl(var(--muted)) h s calc(l + var(--opaque-button-border-intensity)) / alpha);

  /* Fallback for older browsers */
  --accent-border: hsl(var(--accent));
  --accent-border: hsl(from hsl(var(--accent)) h s calc(l + var(--opaque-button-border-intensity)) / alpha);

  /* Fallback for older browsers */
  --destructive-border: hsl(var(--destructive));
  --destructive-border: hsl(from hsl(var(--destructive)) h s calc(l + var(--opaque-button-border-intensity)) / alpha);
}

.dark {
  --button-outline: rgba(255,255,255, .10);
  --badge-outline: rgba(255,255,255, .05);

  --opaque-button-border-intensity: 9;  /* In terms of percentages */

  /* Backgrounds applied on top of other backgrounds when hovered/active */
  --elevate-1: rgba(255,255,255, .04);
  --elevate-2: rgba(255,255,255, .09);

  --background: 215 18% 8%;

  --foreground: 210 20% 92%;

  --border: 215 16% 18%;

  --card: 215 17% 10%;

  --card-foreground: 210 20% 92%;

  --card-border: 215 16% 14%;

  --sidebar: 215 17% 12%;

  --sidebar-foreground: 210 20% 92%;

  --sidebar-border: 215 16% 16%;

  --sidebar-primary: 173 82% 45%;

  --sidebar-primary-foreground: 210 100% 98%;

  --sidebar-accent: 173 15% 16%;

  --sidebar-accent-foreground: 210 20% 92%;

  --sidebar-ring: 173 82% 45%;

  --popover: 215 17% 14%;

  --popover-foreground: 210 20% 92%;

  --popover-border: 215 16% 18%;

  --primary: 173 82% 45%;

  --primary-foreground: 210 100% 98%;

  --secondary: 215 16% 18%;

  --secondary-foreground: 210 20% 92%;

  --muted: 215 16% 16%;

  --muted-foreground: 210 15% 65%;

  --accent: 215 16% 17%;

  --accent-foreground: 210 20% 92%;

  --destructive: 358 75% 48%;

  --destructive-foreground: 210 100% 98%;

  --input: 215 16% 28%;
  --ring: 173 82% 45%;
  --chart-1: 173 82% 55%;
  --chart-2: 183 78% 60%;
  --chart-3: 163 85% 58%;
  --chart-4: 193 75% 62%;
  --chart-5: 153 70% 60%;

  /* Success/Teal accent for confirmations */
  --success: 173 82% 50%;
  --success-foreground: 210 100% 98%;

  /* Warning/Amber accent for alerts */
  --warning: 32 95% 58%;
  --warning-foreground: 215 16% 12%;

  --shadow-2xs: 0px 1px 2px 0px hsl(210 10% 2% / 0.20);
  --shadow-xs: 0px 1px 3px 0px hsl(210 10% 2% / 0.25);
  --shadow-sm: 0px 2px 4px 0px hsl(210 10% 2% / 0.22), 0px 1px 2px -1px hsl(210 10% 2% / 0.18);
  --shadow: 0px 4px 6px -1px hsl(210 10% 2% / 0.28), 0px 2px 4px -2px hsl(210 10% 2% / 0.22);
  --shadow-md: 0px 6px 12px -2px hsl(210 10% 2% / 0.32), 0px 3px 6px -3px hsl(210 10% 2% / 0.26);
  --shadow-lg: 0px 10px 20px -4px hsl(210 10% 2% / 0.38), 0px 4px 8px -4px hsl(210 10% 2% / 0.30);
  --shadow-xl: 0px 20px 30px -6px hsl(210 10% 2% / 0.42), 0px 8px 16px -6px hsl(210 10% 2% / 0.35);
  --shadow-2xl: 0px 25px 50px -12px hsl(210 10% 2% / 0.50);

}

@layer base {
  * {
    @apply border-border;
  }

  body {
    @apply font-sans antialiased bg-background text-foreground;
  }
}

/**
 * Using the elevate system.
 * Automatic contrast adjustment.
 *
 * <element className="hover-elevate" />
 * <element className="active-elevate-2" />
 *
 * // Using the tailwind utility when a data attribute is "on"
 * <element className="toggle-elevate data-[state=on]:toggle-elevated" />
 * // Or manually controlling the toggle state
 * <element className="toggle-elevate toggle-elevated" />
 *
 * Elevation systems have to handle many states.
 * - not-hovered, vs. hovered vs. active  (three mutually exclusive states)
 * - toggled or not
 * - focused or not (this is not handled with these utilities)
 *
 * Even without handling focused or not, this is six possible combinations that
 * need to be distinguished from eachother visually.
 */
@layer utilities {

  /* Hide ugly search cancel button in Chrome until we can style it properly */
  input[type="search"]::-webkit-search-cancel-button {
    @apply hidden;
  }

  /* Placeholder styling for contentEditable div */
  [contenteditable][data-placeholder]:empty::before {
    content: attr(data-placeholder);
    color: hsl(var(--muted-foreground));
    pointer-events: none;
  }

  /* .no-default-hover-elevate/no-default-active-elevate is an escape hatch so consumers of
   * buttons/badges can remove the automatic brightness adjustment on interactions
   * and program their own. */
  .no-default-hover-elevate {}

  .no-default-active-elevate {}


  /**
   * Toggleable backgrounds go behind the content. Hoverable/active goes on top.
   * This way they can stack/compound. Both will overlap the parent's borders!
   * So borders will be automatically adjusted both on toggle, and hover/active,
   * and they will be compounded.
   */
  .toggle-elevate::before,
  .toggle-elevate-2::before {
    content: "";
    pointer-events: none;
    position: absolute;
    inset: 0px;
    /*border-radius: inherit;   match rounded corners */
    border-radius: inherit;
    z-index: -1;
    /* sits behind content but above backdrop */
  }

  .toggle-elevate.toggle-elevated::before {
    background-color: var(--elevate-2);
  }

  /* If there's a 1px border, adjust the inset so that it covers that parent's border */
  .border.toggle-elevate::before {
    inset: -1px;
  }

  /* Does not work on elements with overflow:hidden! */
  .hover-elevate:not(.no-default-hover-elevate),
  .active-elevate:not(.no-default-active-elevate),
  .hover-elevate-2:not(.no-default-hover-elevate),
  .active-elevate-2:not(.no-default-active-elevate) {
    position: relative;
    z-index: 0;
  }

  .hover-elevate:not(.no-default-hover-elevate)::after,
  .active-elevate:not(.no-default-active-elevate)::after,
  .hover-elevate-2:not(.no-default-hover-elevate)::after,
  .active-elevate-2:not(.no-default-active-elevate)::after {
    content: "";
    pointer-events: none;
    position: absolute;
    inset: 0px;
    /*border-radius: inherit;   match rounded corners */
    border-radius: inherit;
    z-index: 999;
    /* sits in front of content */
  }

  .hover-elevate:hover:not(.no-default-hover-elevate)::after,
  .active-elevate:active:not(.no-default-active-elevate)::after {
    background-color: var(--elevate-1);
  }

  .hover-elevate-2:hover:not(.no-default-hover-elevate)::after,
  .active-elevate-2:active:not(.no-default-active-elevate)::after {
    background-color: var(--elevate-2);
  }

  /* If there's a 1px border, adjust the inset so that it covers that parent's border */
  .border.hover-elevate:not(.no-hover-interaction-elevate)::after,
  .border.active-elevate:not(.no-active-interaction-elevate)::after,
  .border.hover-elevate-2:not(.no-hover-interaction-elevate)::after,
  .border.active-elevate-2:not(.no-active-interaction-elevate)::after,
  .border.hover-elevate:not(.no-hover-interaction-elevate)::after {
    inset: -1px;
  }

  /* Animation Keyframes */
  @keyframes fadeIn {
    from { opacity: 0; }
    to { opacity: 1; }
  }

  @keyframes fadeInUp {
    from {
      opacity: 0;
      transform: translateY(20px);
    }
    to {
      opacity: 1;
      transform: translateY(0);
    }
  }

  @keyframes fadeInDown {
    from {
      opacity: 0;
      transform: translateY(-20px);
    }
    to {
      opacity: 1;
      transform: translateY(0);
    }
  }

  @keyframes slideInLeft {
    from {
      opacity: 0;
      transform: translateX(-30px);
    }
    to {
      opacity: 1;
      transform: translateX(0);
    }
  }

  @keyframes slideInRight {
    from {
      opacity: 0;
      transform: translateX(30px);
    }
    to {
      opacity: 1;
      transform: translateX(0);
    }
  }

  @keyframes scaleIn {
    from {
      opacity: 0;
      transform: scale(0.9);
    }
    to {
      opacity: 1;
      transform: scale(1);
    }
  }

  @keyframes shimmer {
    0% { background-position: -1000px 0; }
    100% { background-position: 1000px 0; }
  }

  @keyframes pulse {
    0%, 100% { opacity: 1; }
    50% { opacity: 0.5; }
  }

  @keyframes bounce {
    0%, 100% { transform: translateY(0); }
    50% { transform: translateY(-10px); }
  }

  @keyframes spin {
    from { transform: rotate(0deg); }
    to { transform: rotate(360deg); }
  }

  /* Animation Classes */
  .animate-fadeIn {
    animation: fadeIn 0.3s ease-in-out;
    animation-fill-mode: both;
  }

  .animate-fadeInUp {
    animation: fadeInUp 0.4s ease-out;
    animation-fill-mode: both;
  }

  .animate-fadeInDown {
    animation: fadeInDown 0.4s ease-out;
    animation-fill-mode: both;
  }

  .animate-slideInLeft {
    animation: slideInLeft 0.4s ease-out;
    animation-fill-mode: both;
  }

  .animate-slideInRight {
    animation: slideInRight 0.4s ease-out;
    animation-fill-mode: both;
  }

  .animate-scaleIn {
    animation: scaleIn 0.3s ease-out;
    animation-fill-mode: both;
    opacity: 0;
    transform: scale(0.9);
  }

  .animate-pulse {
    animation: pulse 2s cubic-bezier(0.4, 0, 0.6, 1) infinite;
  }

  .animate-bounce {
    animation: bounce 1s infinite;
  }

  .animate-spin {
    animation: spin 1s linear infinite;
  }

  /* Stagger Animations */
  .stagger-delay-1 { animation-delay: 0.05s; }
  .stagger-delay-2 { animation-delay: 0.1s; }
  .stagger-delay-3 { animation-delay: 0.15s; }
  .stagger-delay-4 { animation-delay: 0.2s; }
  .stagger-delay-5 { animation-delay: 0.25s; }
  .stagger-delay-6 { animation-delay: 0.3s; }

  /* Transition Utilities */
  .transition-smooth {
    transition: all 0.3s cubic-bezier(0.4, 0, 0.2, 1);
  }

  .transition-fast {
    transition: all 0.15s ease-in-out;
  }

  .transition-slow {
    transition: all 0.5s ease-in-out;
  }

  .transition-transform {
    transition: transform 0.3s cubic-bezier(0.4, 0, 0.2, 1);
  }

  .transition-colors {
    transition: background-color 0.2s ease-in-out, color 0.2s ease-in-out, border-color 0.2s ease-in-out;
  }

  /* Hover Transform Effects */
  .hover-lift {
    transition: transform 0.2s ease-in-out, box-shadow 0.2s ease-in-out;
  }

  .hover-lift:hover {
    transform: translateY(-2px);
    box-shadow: var(--shadow-md);
  }

  .hover-scale {
    transition: transform 0.2s ease-in-out;
  }

  .hover-scale:hover {
    transform: scale(1.02);
  }

  .hover-scale-sm {
    transition: transform 0.2s ease-in-out;
  }

  .hover-scale-sm:hover {
    transform: scale(1.01);
  }

  /* Loading Shimmer Effect */
  .shimmer {
    background: linear-gradient(
      90deg,
      hsl(var(--card)) 0%,
      hsl(var(--muted)) 50%,
      hsl(var(--card)) 100%
    );
    background-size: 1000px 100%;
    animation: shimmer 2s infinite linear;
  }

  /* Skeleton Loader */
  .skeleton {
    @apply relative overflow-hidden rounded bg-muted;
  }

  .skeleton::after {
    content: "";
    position: absolute;
    inset: 0;
    background: linear-gradient(
      90deg,
      transparent 0%,
      hsla(var(--background) / 0.1) 50%,
      transparent 100%
    );
    background-size: 200% 100%;
    animation: shimmer 1.5s infinite;
  }

  /* Page Transition Container */
  .page-transition {
    animation: fadeInUp 0.4s ease-out;
  }

  /* Smooth scrolling */
  html {
    scroll-behavior: smooth;
  }

  /* Custom scrollbar */
  ::-webkit-scrollbar {
    width: 8px;
    height: 8px;
  }

  ::-webkit-scrollbar-track {
    background: hsl(var(--muted) / 0.3);
    border-radius: 4px;
  }

  ::-webkit-scrollbar-thumb {
    background: hsl(var(--muted-foreground) / 0.3);
    border-radius: 4px;
  }

  ::-webkit-scrollbar-thumb:hover {
    background: hsl(var(--muted-foreground) / 0.5);
  }
}

